import { Link } from "react-router-dom";
import { Field, reduxForm } from "redux-form";
import submit from "../../submit";

import "./LoginPage.scss";

import { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Navigate, useNavigate } from "react-router-dom";

import { login } from "../../context/slices/auth";
import { clearMessage } from "../../context/slices/message";

const renderField = ({ input, label, type, meta: { touched, error } }) => (
  <div className="form-group">
    <label>{label}</label>
    <div className="d-flex justify-content-center">
      <input
        {...input}
        placeholder={label}
        type={type}
        className="form-control w-50 "
      />
      {touched && error && (
        <div
          className="alert alert-danger d-flex justify-content-center "
          role="alert"
        >
          {error}
        </div>
      )}
    </div>
  </div>
);

// <span className="error-message">{error}</span> &&

const LoginPage = (props) => {
  //const { error, handleSubmit, pristine, reset, submitting } = props;

  let navigate = useNavigate();

  const [loading, setLoading] = useState(false);

  const { isLoggedIn } = useSelector((state) => state.auth);
  const { message } = useSelector((state) => state.message);

  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(clearMessage());
  }, [dispatch]);

  const initialValues = {
    email: "",
    password: "",
  };

  const handleLogin = (formValue) => {
    const { username, password } = formValue;
    setLoading(true);

    dispatch(login({ email, password }))
      .unwrap()
      .then(() => {
        navigate("/");
        window.location.reload();
      })
      .catch(() => {
        setLoading(false);
      });
  };

  if (isLoggedIn) {
    return <Navigate to="/" />;
  }

  return (
    <div className="text-center">
      <div className="form-signin w-100 m-auto">
        <div className="container py-5">
          <h1 className="mb-3 fw-normal">Login</h1>

          {/* <form onSubmit={handleSubmit(submit)}> */}
          <form onSubmit={handleSubmit()}>
            <Field
              name="email"
              type="email"
              component={renderField}
              label="Email"
            />
            <Field
              name="password"
              type="password"
              component={renderField}
              label="Password"
            />
            {error && <strong>{error}</strong>}
            <div className=" gap-2  mx-auto btn-align">
              <button
                className="w-50 btn btn-lg btn-primary"
                type="submit"
                disabled={submitting}
              >
                Log In
              </button>
              <button
                className="w-50 btn btn-lg btn-secondary"
                type="button"
                disabled={pristine || submitting}
                onClick={reset}
              >
                Clear Values
              </button>
            </div>
            <p className="link-to-register">
              Dont have an account? <Link to={"/register"}>Sign In</Link>
            </p>
            <p className="mt-5 mb-3 text-muted">© 2017–2022</p>
          </form>
        </div>
      </div>
    </div>
  );
};

// export default LoginPage;
export default reduxForm({
  form: "submitValidationLoginPage", // a unique identifier for this form
})(LoginPage);
