import axios from "axios";

const API_URL = "http://springtodolistapi.herokuapp.com/";

const login = (email, password) => {
  return axios
    .post(API_URL + "login", {
      email,
      password,
    })
    .then((response) => {
      if (response.data.accessToken) {
        localStorage.setItem("user", JSON.stringify(response.data));
      }
      // localStorage.setItem(
      //   "user",
      //   JSON.stringify(
      //     "eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzUxMiJ9.eyJzdWIiOiJ0ZXN0QGdtYWlsLmNvbSIsImV4cCI6MTY2ODg2NTgzNX0.2XMk5PGHJgH6dR56E7Kp2S7_FW1BvjdzCZ-FqazlfbzZZXggtc3yHryaMGze9j1G1py85pAAkecX5mMf7d0g_g"
      //   )
      // );

      return response.data;
    });
};

const logout = () => {
  localStorage.removeItem("user");
};

const authService = {
  login,
  logout,
};

export default authService;
