import { Link } from "react-router-dom";
import { Field, reduxForm } from "redux-form";
import submit from "../../submit";

import "./LoginPage.scss";

const renderField = ({ input, label, type, meta: { touched, error } }) => (
  <div>
    <label>{label}</label>
    <div>
      <input {...input} placeholder={label} type={type} />
      {touched && error && <span className="error-message">{error}</span>}
    </div>
  </div>
);

const LoginPage = (props) => {
  const { error, handleSubmit, pristine, reset, submitting } = props;
  return (
    <div className="text-center">
      <div className="form-signin w-100 m-auto">
        <div className="container py-5">
          <h1>Login</h1>
          <h2 className="h3 mb-3 fw-normal">Please sign in</h2>
          <form onSubmit={handleSubmit(submit)}>
            <Field
              name="username"
              type="text"
              component={renderField}
              label="Username"
              className="form__input"
            />
            <Field
              name="password"
              type="password"
              component={renderField}
              label="Password"
            />
            {error && <strong>{error}</strong>}
            <div className=" gap-2  mx-auto btn-align">
              <button
                className="w-50 btn btn-lg btn-primary"
                type="submit"
                disabled={submitting}
              >
                Log In
              </button>
              <button
                className="w-50 btn btn-lg btn-secondary"
                type="button"
                disabled={pristine || submitting}
                onClick={reset}
              >
                Clear Values
              </button>
            </div>
            <p className="link-to-register">
              Dont have an account? <Link to={"/register"}>Sign In</Link>
            </p>
            <p className="mt-5 mb-3 text-muted">© 2017–2022</p>
          </form>
        </div>
      </div>
    </div>
  );
};

// export default LoginPage;
export default reduxForm({
  form: "submitValidationLoginPage", // a unique identifier for this form
})(LoginPage);
