import axios from "axios";

const API_URL = "http://springtodolistapi.herokuapp.com/";
const REACT_TOKEN_AUTH =
  "Bearer eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzUxMiJ9.eyJzdWIiOiJ0ZXN0QGdtYWlsLmNvbSIsImV4cCI6MTY2ODg1Njc4Nn0.mwKEhWk25deOUEu4wKG7ohxp-Y-dVQpj44km2DA4mI31jLmEKLM3b03UaxsSHG7qHKagBNsN8Stj9JW-jV06oQ";

const login = (username, password) => {
  return axios
    .post(API_URL + "login", {
      username,
      password,
    })
    .then((response) => {
      // if (response.data.accessToken) {
      // localStorage.setItem("user", JSON.stringify(response.data));
      // }

      localStorage.setItem("user", "REACT_TOKEN_AUTH");

      return response.data;
    });
};
const logout = () => {
  localStorage.removeItem("user");
};

const authService = {
  login,
  logout,
};

export default authService;
